sys_variable_value.value:
(function execute(inputs, outputs) {
  // This code will retreive the values in 

  if (inputs.recID) {
	var current = new GlideRecord('x_btal_bt_learning_lp_course');
    current.addQuery('sys_id', inputs.recID);
    current.query();

    if (current.next()) {
      outputs.unit_list+="Current Record Found | ";
		var vari = current.variables;
      	outputs.unit_list+= current.name + " | ";
    }
  } /*else {
    if(inputs.course) {
      var current = inputs.course;
      if (current.next()) {
        var vari = current.variables;
      }
    } else {
      outputs.unit_list = ""
    }
  */
  if(vari) {   
    	var mrvs = vari.lp_course_units;
  		outputs.unit_list += mrvs.getRowCount();
  } else {
   outputs.unit_list += "No variable object apparently"; 
  }

  /*
	gs.include('x_btal_bt_learning.LP_Utils_server');
	var lp = new x_btal_bt_learning.LP_Utils_server(); 

	var getRows = lp.Get_Courses_from_MRVS(gr);
    outputs.unit_list = getRows;
  } else {
    outputs.unit_list = 'No rows found';
  }*/
})(inputs, outputs);

