sys_variable_value.value:
(function execute(inputs, outputs) {
// For quizzes, we have to add the user to the available group table.  Otherwise, they can't be assigned the quiz.
//If the user has already been added to the assessment category user table, don't add them again.
  if(inputs.Count <1) {
    var grACU = new GlideRecord('asmt_m2m_category_user')
    grACU.user = inputs.User;
    grACU.metric_category = inputs.MetCatID;
    grACU.insert();
  }

   var instanceId = new global.AssessmentUtils().createAssessments(inputs.Quiz, "", inputs.User);
	outputs.quiz_instance = instanceId.toString().substring(0,32);
  //var gr = new GlideRecord("asmt_assessment_instance");
  //var success = gr.get(instanceId.substring(0,32));
  //if(success) {

  //} else {
  //  outputs.quiz_instance = '0';
  //}

})(inputs, outputs);

